import React, { Component } from 'react';

export class GitHubUser extends Component {
  componentDidMount() {
    // params.login is path='/user/:login' from the Router App.js
    // The parameter of getUser() is getting from the async username parameter
    this.props.getUser(this.props.match.params.login);
  }

  render() {
    const {
      name,
      avatar_url,
      location,
      bio,
      blog,
      login,
      html_url,
      followers,
      following,
      public_repos,
      public_gists,
      hireable
    } = this.props.user;

    const { loading } = this.props;

    return <div>{name}</div>;
  }
}

export default GitHubUser;
