import React, { Component } from 'react';
import { NavBar } from './components/layout/Navbar.component';
import { UserList } from './components/users/UserList.component';
import { Search } from './components/users/Search.component';

import './App.css';

class App extends Component {
  state = {
    users: [],
    loading: false
  };

  async componentDidMount() {
    try {
      // setState() is to update the original state
      this.setState({ loading: true });

      const res = await fetch(
        `https://api.github.com/users?client_id=${process.env.REACT_APP_GITHUB_CLIENT_ID}&client_secret=${process.env.REACT_APP_GITHUB_CLIENT_SECRET}`
      );
      const data = await res.json();
      this.setState({ users: data, loading: false });
    } catch (e) {
      console.log(e);
    }
  }

  // Search GitHub Users
  searchUsers = async txt => {
    const res = await fetch(
      `https://api.github.com/users?client_id=${process.env.REACT_APP_GITHUB_CLIENT_ID}&client_secret=${process.env.REACT_APP_GITHUB_CLIENT_SECRET}`
    );
    const data = await res.json();
    this.setState({ users: data, loading: false });
  };

  render() {
    return (
      <div className='App'>
        <NavBar />
        <div className='container'>
          <Search searchUsers={this.searchUsers} />
          <UserList loading={this.state.loading} github={this.state.users} />
        </div>
      </div>
    );
  }
}

export default App;
